import React from "react";


function Form(props){
return (
  <div className="container">
  <form>
    <div className="form-group">
        {/* <label htmlFor="search"><h2>Search for Books of Interest and Save </h2></label> */}
        <h1 style={{ fontSize: "2.1em", color: "red" }}>
              Something Very <strong > Magical</strong> can happen when you 
            </h1>
      <input
        onChange={props.handleInputChange}
        value={props.search}
        name="search"
        type="text"
        className="form-control"
        placeholder="Search a Book"
        id="search"
      />
      <button onClick={props.handleFormSubmit} className="btn btn-dark mt-3 mb-5">
        Search
        </button>
    </div>
  </form>
  </div>
);
}

export default Form;
